mapper.select:
SELECT EM_NAME, EM_EMAIL
FROM ENTERPRISE_MEMBER
WHERE CM_EMAIL = #{email}
AND EM_PASSWORD = #{pwd}

mapper.update:
UPDATE ENTERPRISE_MEMBER
EM_NAME = #{name}, EM_EMAIL = #{email}, EM_MOD_DATE = SYSDATE
WHERE EM_NO = #{no}

UPDATE JOBPOSTING
JOBPOSTING_TITLE = #{title}, EMPLOYMENT_TYPE = #{type}, LOCATION_NAME = #{location_name}, WORKING_HOUR = #{working_hour}, SALARY = #{salary}, GENDER = #{gender}, AGE = #{age}, EDUCATION = #{education}, CAREER = #{career}, PRIMARY_DUTIES1 = #{primary_duties1}, PRIMARY_DUTIES2 = #{primary_duties2}, PRIMARY_DUTIES3 = #{primary_duties3}, RECRUITER_NAME = #{recruiter_name}, RECRUITER_PHONE = #{recruiter_phone}, RECRUITER_EMAIL = #{recruiter_email}, RECRUITMENT_PROCESS = #{recruiter_process}, EMPLOYEE_BENEFIT = #{employee_benefit}, ADDRESS = #{address}, APPLICATION_PERIOD = #{application_period}, CREATE_DATE = SYSDATE
WHERE JOBPOSTING_NO = #{no}

mapper.insert:
 
    SELECT JOBPOSTING_NO_SEQ.NEXTVAL
   FROM DUAL
 
INSERT INTO JOBPOSTING
VALUE(JOBPOSTING_NO, JOBPOSTING_TITLE, EMPLOYMENT_TYPE
, LOCATION_NAME, WORKING_HOUR, SALARY, GENDER, AGE, EDUCATION, CAREER
, PRIMARY_DUTIES1, PRIMARY_DUTIES2, PRIMARY_DUTIES3, RECRUITER_NAME
, RECRUITER_PHONE, RECRUITER_EMAIL, RECRUITMENT_PROCESS, EMPLOYEE_BENEFIT
, ADDRESS, APPLICATION_PERIOD, CREATE_DATE, EM_NO, EM_NAME)
VALUES(#{no}, #{title}, #{type}, #{location_name}, #{working_hour}
, #{salary}, #{gender}, #{age}, #{education}, #{career}, #{primary_duties1}
, #{primary_duties2}, #{primary_duties3}, #{recruiter_name}, #{recruiter_phone}
, #{recruiter_email}, #{recruiter_process}, #{employee_benefit}, #{address}
, #{application_period}, SYSDATE, #{em_no}, #{em_name})
