mapper.select:
SELECT CM_NAME, CM_EMAIL
FROM COMMON_MEMBER
WHERE CM_EMAIL = #{email}
AND CM_PASSWORD = #{pwd}

SELECT CM_NO, CM_NAME, CM_EMAIL, CM_JOIN_DATE
FROM COMMON_MEMBER
WHERE MNO = #{no}

mapper.update:
UPDATE COMMON_MEMBER
CM_NAME = #{name}, CM_EMAIL = #{email}, CM_MOD_DATE = SYSDATE
WHERE CM_NO = #{no}
