mapper.select:
SELECT CM_NAME, CM_EMAIL
FROM COMMON_MEMBER
WHERE CM_EMAIL = #{email}
AND CM_PASSWORD = #{pwd}

SELECT CM_NO, CM_NAME, CM_EMAIL, CM_JOIN_DATE
FROM COMMON_MEMBER
WHERE MNO = #{no}

mapper.update:
UPDATE COMMON_MEMBER
CM_NAME = #{name}, CM_EMAIL = #{email}, CM_MOD_DATE = SYSDATE
WHERE CM_NO = #{no}

mapper.select:
SELECT COUNT(*)
FROM COMMON_MEMBER
WHERE CM_NICKNAME = #{nickName}

mapper.update:
UPDATE RESUME
NAME = #{name}, CAREER_STATUS = #{career}, DESIRE_ANNUAL_SALARY = #{salary}, GENDER = #{gender}, BIRTH_DATE = #{birth_date}, PHONE_NUMBER = #{phone_no}, EMAIL = #{email}, ADDRESS = #{address}, FINAL_ACADEMIC_BACKGROUND = #{final_academic_background}, EDUCATIONAL_INSTITUTE = #{educational_institute}, MAJOR = #{major}, GRADUATION_STATUS = #{graduation_status}, ENTRANCE_DATE = #{entrance_date}, GRADUATION_DATE = #{graduation_date}, LICENSE_NAME = #{license_name}, LICENSE_ISSUING_AGENCY = #{license_issuing_agency}, LICENSE_PASSING_DATE = #{passing_date}, SELF_INTRODUCTION = #{self_introduction}, CM_NO = #{cm_no}, WRITE_DATE = SYSDATE
WHERE RESUME_NO = #{no}

mapper.insert:

    SELECT RESUME_NO_SEQ.NEXTVAL
   FROM DUAL
 
INSERT INTO RESUME
(RESUME_NO, NAME, CAREER_STATUS, DESIRE_ANNUAL_SALARY, GENDER, BIRTH_DATE
, PHONE_NUMBER, EMAIL, ADDRESS, FINAL_ACADEMIC_BACKGROUND, EDUCATIONAL_INSTITUTE
, MAJOR, GRADUATION_STATUS, ENTRANCE_DATE, GRADUATION_DATE, LICENSE_NAME
, LICENSE_ISSUING_AGENCY, LICENSE_PASSING_DATE, SELF_INTRODUCTION, CM_NO, WRITE_DATE)
VALUES(#{no}, #{name}, #{career}, #{salary}, #{gender}, #{birth_date}
, #{phone_no}, #{email}, #{address}, #{final_academic_background}
, #{educational_institute}, #{major}, #{graduation_status}, #{entrance_date}
, #{graduation_date}, #{license_name}, #{license_issuing_agency}, #{passing_date}
, #{self_introduction}, #{cm_no}, SYSDATE)
