mapper.update:
UPDATE BOARD
BOARD_TITLE = #{title}, BOARD_COTENT = #{content}, BOARD_MOD_DATE = SYSDATE
WHERE BOARD_NO = #{no}

UPDATE CS_BOARD
BOARD_TITLE = #{title}, BOARD_COTENT = #{content}, REPLY_DATE = SYSDATE
WHERE CS_BOARD_NO = #{no}

mapper.select:
		SELECT COUNT(*)
		FROM BOARD
        WHERE CATEGORY = #{category}
